import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ProgramsSection = () => {
  const [projects, setProjects] = useState([]);

  useEffect(() => {
    loadProjects();
  }, []);

  const loadProjects = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/projects/published');
      setProjects(response.data);
    } catch (error) {
      console.error('Error loading projects:', error);
      // Fallback to empty array if API fails
      setProjects([]);
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'completed': return 'bg-green-100 text-green-800';
      case 'ongoing': return 'bg-blue-100 text-blue-800';
      case 'upcoming': return 'bg-yellow-100 text-yellow-800';
      case 'future': return 'bg-gray-100 text-gray-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getStatusLabel = (status) => {
    return status.charAt(0).toUpperCase() + status.slice(1);
  };

  return (
    <section className="p-8">
      <h2 className="text-3xl font-bold mb-4">Our Programs</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
        <div className="p-4 border rounded">
          <h3>Menstrual Health Education</h3>
          <p>Workshops and awareness on hygiene and infections.</p>
        </div>
        <div className="p-4 border rounded">
          <h3>Reusable Pad Training</h3>
          <p>Empowering women with sewing and entrepreneurship skills.</p>
        </div>
        <div className="p-4 border rounded">
          <h3>Community Engagement</h3>
          <p>Mentorship and conversations to end menstrual stigma.</p>
        </div>
        <div className="p-4 border rounded">
          <h3>Sustainability Projects</h3>
          <p>Creating cooperatives for pad-making.</p>
        </div>
      </div>

      {projects.length > 0 && (
        <>
          <h2 className="text-3xl font-bold mb-4 mt-12">Projects Done or To Come</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {projects.map((project) => (
              <div key={project._id} className="p-6 border rounded-lg shadow-md">
                {project.image && (
                  <img
                    src={project.image}
                    alt={project.title}
                    className="w-full h-48 object-cover rounded-md mb-4"
                  />
                )}
                <h3 className="text-xl font-semibold mb-2">
                  {getStatusLabel(project.status)}: {project.title}
                </h3>
                <div className="flex items-center mb-2">
                  <span className={`px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(project.status)} mr-2`}>
                    {getStatusLabel(project.status)}
                  </span>
                  <span className="text-sm text-gray-600">{project.timeline}</span>
                </div>
                <p>{project.description}</p>
              </div>
            ))}
          </div>
        </>
      )}
    </section>
  );
};

export default ProgramsSection;
