import { useEffect, useState } from 'react';

const Chatbot = () => {
  const [isLoaded, setIsLoaded] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Load BotLibre SDK scripts
    const sdkScript = document.createElement('script');
    sdkScript.src = 'https://www.botlibre.com/scripts/sdk.js';
    sdkScript.async = true;

    const gameSdkScript = document.createElement('script');
    gameSdkScript.src = 'https://www.botlibre.com/scripts/game-sdk.js';
    gameSdkScript.async = true;

    // Initialize BotLibre chatbot after scripts load
    const initChatbot = () => {
      try {
        if (window.SDKConnection && window.WebChatbotListener) {
          console.log('Initializing BotLibre chatbot...');

          window.SDK.applicationId = "1684363762437973484";
          window.SDK.backlinkURL = "http://www.botlibre.com/login?affiliate=666228";

          const sdk = new window.SDKConnection();
          const web = new window.WebChatbotListener();

          web.connection = sdk;
          web.instance = "59486614";
          web.instanceName = "Heritage Guide";
          web.prefix = "botplatform";
          web.caption = "Chat with Heritage Guide";
          web.boxLocation = "bottom-right";
          web.color = "#009900";
          web.background = "#fff";
          web.css = "https://www.botlibre.com/css/chatlog.css";
          web.gameSDKcss = "https://www.botlibre.com/css/game-sdk.css";
          web.buttoncss = "https://www.botlibre.com/css/blue_round_button.css";
          web.version = 8.5;
          web.bubble = true;
          web.backlink = true;
          web.showMenubar = true;
          web.showBoxmax = true;
          web.showSendImage = true;
          web.showChooseLanguage = true;
          web.avatar = true;
          web.chatLog = true;
          web.popupURL = "https://heritageguide.botlibre.com";

          web.createBox();
          setIsLoaded(true);
          console.log('BotLibre chatbot initialized successfully');
        } else {
          console.log('BotLibre SDK not available, retrying...');
          setTimeout(initChatbot, 1000);
        }
      } catch (err) {
        console.error('Error initializing chatbot:', err);
        setError(err.message);
      }
    };

    // Wait for scripts to load then initialize
    let sdkLoaded = false;
    let gameSdkLoaded = false;

    sdkScript.onload = () => {
      sdkLoaded = true;
      console.log('BotLibre SDK loaded');
      if (gameSdkLoaded) {
        initChatbot();
      }
    };

    sdkScript.onerror = () => {
      console.error('Failed to load BotLibre SDK');
      setError('Failed to load SDK');
    };

    gameSdkScript.onload = () => {
      gameSdkLoaded = true;
      console.log('BotLibre Game SDK loaded');
      if (sdkLoaded) {
        initChatbot();
      }
    };

    gameSdkScript.onerror = () => {
      console.error('Failed to load BotLibre Game SDK');
      setError('Failed to load Game SDK');
    };

    // Add scripts to document
    document.head.appendChild(sdkScript);
    document.head.appendChild(gameSdkScript);

    // Fallback timeout in case scripts don't load properly
    setTimeout(() => {
      if (!sdkLoaded || !gameSdkLoaded) {
        console.log('Scripts not loaded within timeout, attempting initialization anyway...');
        initChatbot();
      }
    }, 5000);

    // Cleanup function
    return () => {
      // Remove scripts if component unmounts
      if (sdkScript.parentNode) {
        sdkScript.parentNode.removeChild(sdkScript);
      }
      if (gameSdkScript.parentNode) {
        gameSdkScript.parentNode.removeChild(gameSdkScript);
      }
    };
  }, []);

  // Show loading state or error
  if (error) {
    return (
      <div style={{
        position: 'fixed',
        bottom: '20px',
        right: '20px',
        background: '#ff6b6b',
        color: 'white',
        padding: '10px',
        borderRadius: '5px',
        zIndex: 1000,
        fontSize: '12px'
      }}>
        Chatbot Error: {error}
      </div>
    );
  }

  if (!isLoaded) {
    return (
      <div style={{
        position: 'fixed',
        bottom: '20px',
        right: '20px',
        background: '#4CAF50',
        color: 'white',
        padding: '10px',
        borderRadius: '5px',
        zIndex: 1000,
        fontSize: '12px'
      }}>
        Loading Heritage Guide...
      </div>
    );
  }

  return null; // BotLibre creates its own floating chat widget
};

export default Chatbot;
